oldUSDC_token_balance = 0
loop_int = 5 #in seconds

# Notification parameters
alertMinBalance = 100

def get_USDC_balance():
    global USDC_token_balance
    USDC_token = w3.eth.contract(address=USDC, abi=USDC_ABI)
    USDC_token_balance = USDC_token.functions.balanceOf(seniorPoolAddress).call()/1e6
    mydict.update({"GF Reserves (USDC):": USDC_token_balance}) 
    return USDC_token_balance

def status_command(update, context):
    text = "USDC balance: {}".format(USDC_token_balance)
    requests.get(telegram_url, params={"text": text})

if __name__ == '__main__':
    updater = Updater(API_KEY, use_context=True)
    dp = updater.dispatcher
    while True: 
        time.sleep(loop_int) 
        get_USDC_balance()
        if oldUSDC_token_balance == 0:
                text = "First run! USDC balance is {}.".format(USDC_token_balance)
                requests.get(telegram_url, params={"text": text})                   
        elif 0 != USDC_token_balance != oldUSDC_token_balance:
                if abs(oldUSDC_token_balance-USDC_token_balance) >= alertMinBalance:
                    text = "Balance changed {} to {}.".format(oldUSDC_token_balance,USDC_token_balance)
                    requests.get(telegram_url, params={"text": text})
        else:
            pass
        oldUSDC_token_balance = USDC_token_balance

        # Commands
        dp.add_handler(CommandHandler('status', status_command))
        updater.start_polling(1.0) # Run the bot        
